mapper.update:
update user 

    password = #{password},


    name = #{name},


    gender = #{gender},


    createTime = #{createTime}


    updateTime = #{updateTime}


    createUser = #{createUser}


    updateUser = #{updateUser}


where id = #{id} and username = #{username};

mapper.select:
select * from user


        id = #{id}


        and ps = #{ps}


        and sex = #{sex}


        and Date(finalLogin) = #{finalLogin}


        and name = #{name}
